ij_df %>% gather("io_rates", "io_value", 4:6)  %>% gather("nonio_rates", "nonio_value", 7:9)  %>% ggplot(aes(x=io_rates, y = io_value)) + geom_violin(fill = "red", alpha = 0.5) + geom_violin(aes(y=nonio_value), fill = "yellow", alpha = 0.4) + ggtitle('Electricity Rates For Zipcodes Catered By\n Both Investor Owned Utilities And Non-Investor Owned Utilities') + theme(plot.title=element_text(size=9, face="bold", vjust=2)) + labs(x="Rates", y="Value($)") 