ij_df %>% gather("io_rates", "io_value", 4:6)  %>% gather("nonio_rates", "nonio_value", 7:9)  %>% ggplot(aes(x=io_rates, y = io_value)) + geom_violin(aes(color = 'Investor Owned Utilities'), alpha = 0.7) + geom_violin(aes(y=nonio_value, color = 'Non-Investor Owned Utilities'), alpha = 0.5) +  scale_colour_manual(name='', values=c('Investor Owned Utilities'='red', 'Non-Investor Owned Utilities'='blue')) + ggtitle('Electricity Rates For Zipcodes Catered By\n Both Investor Owned Utilities And Non-Investor Owned Utilities') + theme(plot.title=element_text(size=13, face="bold", vjust=2)) + labs(x="Rates", y="Value($)") + coord_flip() + theme(legend.position="bottom")

