io_df %>% gather("io_rates", "io_value", 4:6) %>% ggplot(aes(x = io_value, y = state, color = state)) + geom_point() + facet_wrap(~io_rates) + geom_jitter() + scale_x_continuous(limits=c(1,300)) + labs(x="Value($)", y="State") + ggtitle('Investor Owned Utilities: Rate Distributions Among States') + theme(plot.title=element_text(size=9, face="bold", vjust=2)) + theme(legend.position="none") + theme(axis.text.x=element_text(angle=50, size=10, vjust=0.5))